<?php
/**
 * Dasboard widgets.
 *
 * @package Briar
 * @since 1.0
 */

/**
 * Slicejack dashboard metabox
 */
function sj_dashboard_setup() {
	add_meta_box( 'dashboard_slicejack', __( 'Slicejack News', 'pine' ), 'sj_dashboard_widget', 'dashboard', 'side', 'high' );
}
add_action( 'wp_dashboard_setup', 'sj_dashboard_setup' );

/**
 * SliceJack News dashboard widget.
 *
 * @since 1.0
 */
function sj_dashboard_widget() {
	global $slicejack_url;

	$feed = array(
		'type' => '',
		'link' => $slicejack_url,
		'url' => $slicejack_url . '?feed=rss2',
		'title' => __( 'SliceJack Blog', 'pine' ),
		'items' => 5,
		'show_summary' => 1,
		'show_author' => 0,
		'show_date' => 1,
	);

	define( 'DOING_AJAX', true );

	wp_dashboard_cached_rss_widget( 'dashboard_slicejack', 'sj_dashboard_widget_output', $feed );
}

/**
 * Display the SliceJack news feed.
 *
 * @since 1.0
 *
 * @param string $widget_id Widget ID.
 * @param array  $feed     Array of informations about RSS feed.
 */
function sj_dashboard_widget_output( $widget_id, $feed ) {
	global $slicejack_url; ?>
	<div class="rss-widget">
		<div style="display: block; text-align: center; margin: 20px 0 30px 0;">
			<a href="<?php echo esc_url( $slicejack_url ); ?>" target="_blank" style="display: inline-block;">
				<img src="data:image/png;base64," />
			</a>
		</div>
		<?php sj_widget_rss_output( $feed['url'], $feed ); ?>
		<div style="display: block; text-align: center; margin-top: 30px;">
			<a href="<?php echo esc_url( $slicejack_url . '/blog/' ); ?>" style="display: inline-block; width: 150px; height: 32px; line-height: 32px; color: #777; border: solid 1px #777; border-radius: 2px; text-align: center; text-decoration: none; font-size: 13px;"><?php esc_html_e( 'Read more posts', 'pine' ) ?></a>
		</div>
	</div>
	<?php
}

/**
 * Display the RSS entries in a list.
 *
 * @since 2.5.0
 *
 * @param string|array|object $rss RSS url.
 * @param array               $args Widget arguments.
 */
function sj_widget_rss_output( $rss, $args = array() ) {
	if ( is_string( $rss ) ) {
		$rss = fetch_feed( $rss );
	} elseif ( is_array( $rss ) && isset( $rss['url'] ) ) {
		$args = $rss;
		$rss = fetch_feed( $rss['url'] );
	} elseif ( ! is_object( $rss ) ) {
		return;
	}

	if ( is_wp_error( $rss ) ) {
		if ( is_admin() || current_user_can( 'manage_options' ) ) {
			echo '<p>' . sprintf( esc_html__( '<strong>RSS Error</strong>: %s', 'pine' ), esc_html( $rss->get_error_message() ) ) . '</p>';
		}
		return;
	}

	$default_args = array( 'show_author' => 0, 'show_date' => 0, 'show_summary' => 0, 'items' => 0 );
	$args = wp_parse_args( $args, $default_args );

	$items = (int) $args['items'];
	if ( $items < 1 || 20 < $items ) {
		$items = 10;
	}
	$show_summary  = (int) $args['show_summary'];
	$show_author   = (int) $args['show_author'];
	$show_date     = (int) $args['show_date'];

	if ( ! $rss->get_item_quantity() ) {
		echo '<ul><li>' . esc_html__( 'An error has occurred, which probably means the feed is down. Try again later.', 'pine' ) . '</li></ul>';
		$rss->__destruct();
		unset( $rss );
		return;
	}

	echo '<ul>';
	foreach ( $rss->get_items( 0, $items ) as $item ) {
		$link = $item->get_link();
		while ( stristr( $link, 'http' ) !== $link ) {
			$link = substr( $link, 1 );
		}
		$link = strip_tags( $link );

		$title = trim( strip_tags( $item->get_title() ) );
		if ( empty( $title ) ) {
			$title = __( 'Untitled', 'pine' );
		}

		$desc = html_entity_decode( $item->get_description(), ENT_QUOTES, get_option( 'blog_charset' ) );
		$desc = esc_attr( wp_trim_words( $desc, 55, ' [&hellip;]' ) );

		$summary = '';
		if ( $show_summary ) {
			$summary = $desc;

			// Change existing [...] to [&hellip;].
			if ( '[...]' === substr( $summary, -5 ) ) {
				$summary = substr( $summary, 0, -5 ) . '[&hellip;]';
			}

			$summary = '<div class="rssSummary">' . esc_html( $summary ) . '</div>';
		}

		$date = '';
		if ( $show_date ) {
			$date = $item->get_date( 'U' );

			if ( $date ) {
				$date = ' <span class="rss-date">' . date_i18n( get_option( 'date_format' ), $date ) . '</span>';
			}
		}

		$author = '';
		if ( $show_author ) {
			$author = $item->get_author();
			if ( is_object( $author ) ) {
				$author = $author->get_name();
				$author = ' <cite>' . esc_html( strip_tags( $author ) ) . '</cite>';
			}
		}

		$read_more = '';
		if ( '' !== $link ) {
			$read_more = '<a href="' . esc_url( $link ) . '" style="display:block;width:105px;height:22px;line-height:22px;color:#777;border: solid 1px #777;border-radius:2px;text-align:center;text-decoration:none;margin-top:10px;font-size:11px">' . __( 'Read more', 'pine' ) . '</a>';
		}

		if ( '' === $link ) {
			echo '<li>' . esc_html( $title ) . wp_kses( $date, array( 'span' => array( 'class' => true ) ) ) . wp_kses( $summary, array( 'div' => array( 'class' => true ) ) ) . wp_kses( $author, array( 'cite' ) ) . '</li>';
		} elseif ( $show_summary ) {
			echo '<li><a class="rsswidget" href="' . esc_url( $link ) . '">' . esc_html( $title ) . '</a>' . wp_kses( $date, array( 'span' => array( 'class' => true ) ) ) . wp_kses( $summary, array( 'div' => array( 'class' => true ) ) ) . wp_kses( $author, array( 'cite' ) ) . wp_kses( $read_more, array( 'a' => array( 'href' => array(), 'style' => array() ) ) ) . '</li>';
		} else {
			echo '<li><a class="rsswidget" href="' . esc_url( $link ) . '">' . esc_html( $title ) . '</a>' . wp_kses( $date, array( 'span' => array( 'class' => true ) ) ) . wp_kses( $author, array( 'cite' ) ) . wp_kses( $read_more, array( 'a' => array( 'href' => true, 'style' => true ) ) ) . '</li>';
		}
	}
	echo '</ul>';
	$rss->__destruct();
	unset( $rss );
}
